struct aggr* (*get_aggr_fptr(bool ifgrouper,
                             uint64_t op))(char **records,
                                           char *group_aggregation,
                                           size_t num_records,
                                           size_t field_offset,
                                           bool if_aggr_common) {
  
  struct aggr* (*aggr_function)(char **records,
                                char *group_aggregation,
                                size_t num_records,
                                size_t field_offset,
                                bool if_aggr_common) = NULL;
  
  if(!ifgrouper) {
    /* cases for the filter-stage */
    switch (op) {
      case RULE_S1_8:
        aggr_function = aggr_static_uint8_t;
        break;
      case RULE_S1_16:
        aggr_function = aggr_static_uint16_t;
        break;
      case RULE_S1_32:
        aggr_function = aggr_static_uint32_t;
        break;
      case RULE_S1_64:
        aggr_function = aggr_static_uint64_t;
        break;
    }
  } 
  else {
    /* cases for the grouper-stage */
    switch (op) {
      case RULE_S1_8:
        aggr_function = aggr_static_uint8_t;
        break;
      case RULE_S1_16:
        aggr_function = aggr_static_uint16_t;
        break;
      case RULE_S1_32:
        aggr_function = aggr_static_uint32_t;
        break;
      case RULE_S1_64:
        aggr_function = aggr_static_uint64_t;
        break;
    }
  }
  return aggr_function;
}

struct grouper_type* get_gtype(uint64_t op) {
  
  /* free'd just before calling grouper_aggregations(...) */
  struct grouper_type* gtype = calloc(1, sizeof(struct grouper_type));
  if(gtype == NULL)
    errExit("calloc"); 
  switch (op) {
    case RULE_S2_8:
      gtype->qsort_comp = comp_uint8_t;
      gtype->bsearch = bsearch_uint8_t;
      gtype->bsearch = bsearch_uint8_t;
      gtype->alloc_uniqresult = alloc_uniqresult_uint8_t;
      gtype->dealloc_uniqresult = dealloc_uniqresult_uint8_t;
      gtype->get_uniq_record = get_uniq_record_uint8_t;
      break;
    case RULE_S2_16:
      gtype->qsort_comp = comp_uint16_t;
      gtype->bsearch = bsearch_uint16_t;
      gtype->bsearch = bsearch_uint16_t;
      gtype->alloc_uniqresult = alloc_uniqresult_uint16_t;
      gtype->dealloc_uniqresult = dealloc_uniqresult_uint16_t;
      gtype->get_uniq_record = get_uniq_record_uint16_t;
      break;
    case RULE_S2_32:
      gtype->qsort_comp = comp_uint32_t;
      gtype->bsearch = bsearch_uint32_t;
      gtype->bsearch = bsearch_uint32_t;
      gtype->alloc_uniqresult = alloc_uniqresult_uint32_t;
      gtype->dealloc_uniqresult = dealloc_uniqresult_uint32_t;
      gtype->get_uniq_record = get_uniq_record_uint32_t;
      break;
    case RULE_S2_64:
      gtype->qsort_comp = comp_uint64_t;
      gtype->bsearch = bsearch_uint64_t;
      gtype->bsearch = bsearch_uint64_t;
      gtype->alloc_uniqresult = alloc_uniqresult_uint64_t;
      gtype->dealloc_uniqresult = dealloc_uniqresult_uint64_t;
      gtype->get_uniq_record = get_uniq_record_uint64_t;
      break;
  }
  return gtype;
}

